const initialState={
    mypost:null
};
const postReducers=(state={initialState,mypost},actions)=>{
    switch(actions.type){  
        case 'MYPOST':
            return{
                ...state,
                mypost:actions.payload.mypost
            }
        default:
            return state;
    }
}

export default postReducers;
