import React, { Component } from 'react'
import { Link } from 'react-router-dom'
import axios from 'axios';
import M from 'materialize-css'

class SignIn extends Component {
    state={
        userfield:'',
        password:'',
        passwordType:'password'
    }

    handleChange=(e)=>{
        this.setState({
            [e.target.id]:e.target.value

        });
    }


    handleSubmit=(e)=>{
        e.preventDefault();
        axios.post('http://192.168.43.13:8080/api/auth/signin',this.state,{headers:{'Content-Type': 'application/json'}})
            .then(res=>{
                console.log(res.data);
            })
            .catch(error=>{
                M.toast({html: error.response.data.message,classes:"red"})
            })

    }

    handleClick = () => {
        var type=this.state.passwordType==='password'?'text':'password';
        this.setState({
            passwordType:type
        });
    }

    render() {
        return (
       <div>
            <div className="mycard">
                <div className="card-panel white auth-card" >
                    <div className="card-title">
                        Instagram
                    </div>
                    <form onSubmit={this.handleSubmit} noValidate>
                        <div className="card-body">
                            <div className="input-field col s12 border">
                                <input id="userfield" type="text" className="validate input" onChange={this.handleChange}/>
                                <label htmlFor="userfield">username, or email</label>
                            </div>
                            <div className="input-field col s12 border margin" style={{display:'flex'}}>
                                <input id="password" type={this.state.passwordType} className="validate input" onChange={this.handleChange}/>
                                <label htmlFor="password">Password</label>
                                {this.state.passwordType!='password'?<i style={{marginTop:"8px"}} className="material-icons" onClick={this.handleClick}>lock_open</i>:<i style={{marginTop:"8px"}} onClick={this.handleClick} className="material-icons">lock_outline</i>}
                            </div>
                            <div className=" col s12">
                                <button className="button" type="submit" name="action">Log In
                                </button>
                            </div>
                            <br/>
                            <br/>
                            <hr/>
                            <br/>
                            <p><Link to="/">Forgot Password?</Link></p>

                        </div>
                    </form>
                </div>
            </div>
            <div className="mycard">
                <div className="card-panel white auth-card" >
                    <div className="card-body">Don't have an account? <Link to="/signup" className="signup">Sign up</Link></div>
                </div>
            </div>
       </div>
        )
    }
}


export default SignIn;
