const initialState={
    authError:null,

}

const authReducers=(state={initialState},actions)=>{
    switch(actions.type){
        case 'LOGIN_ERROR':
            console.log('login error');
            return{
                ...state,
                authError:'Login Failed'
            }
        case 'LOGIN_SUCCESS':
            console.log('login success');
            return{
                ...state,
                authError:null
            }
        case 'SIGNOUT':
            console.log('signout succesfull');
            return state;
        case 'SIGNUP_SUCCESS':
            console.log('hello',state);
            state.history.push('/');
            return{
                ...state
            }
        case 'SIGNUP_ERROR':
            console.log('hello',state);
            return{
                ...state
            }
        default:
            return state;
    }
}

export default authReducers;
