import React, { Component } from 'react'
import { Link } from 'react-router-dom';
import Dropzone from 'react-dropzone';
import FormData from 'form-data'
import axios from 'axios';

class  NewPost extends Component {
    state={
        ImageUrl:'',
        selectedFile:''
    }

    onImageDrop=(e)=>{
        console.log(e.target.files);
        let data = new FormData();
        data.append('file', e.target.files[0],e.target.files[0].name);
       axios.post('http://localhost:8080/api/post/image-upload', data, {
        headers: {
          'content-type': 'multipart/form-data',
          'auth-token':localStorage.getItem('jwt')
        }
      })
        .then((response) => {
          console.log(response);
        }).catch((error) => {
          console.log(error);
        });

    }
    render(){
    return (
        <div>
            <div className="mycard">
                <div className="card-panel white auth-card" >
                    <div className="card-title">
                        New Post
                    </div>
                    <form  noValidate>
                        <div className="card-body">
                        <div className="row">
                            <div className="column" style={{width:"30%",marginTop:"15px",fontSize:"18px"}}>
                                <span>Location:</span>
                            </div>
                            <div className="column" style={{width:"70%"}}>
                                <div className="input-field col s12 border">
                                    <input id="userfield" type="text"  style={{marginLeft:'10px'}} className="validate input" />
                                </div>
                            </div>
                        </div>
                        <div className="row">
                            <div className="column" style={{width:"30%",marginTop:"15px",fontSize:"18px"}}>
                                <span>Caption:</span>
                            </div>
                            <div className="column" style={{width:"70%"}}>
                                <div className="input-field col s12 border">
                                    <input id="userfield"  type="text"  style={{marginLeft:'10px'}} className="validate input" />
                                </div>
                            </div>
                        </div>
                        <br/>

                        <input type="file" name="image" id='image' onChange={this.onImageDrop}/>
                            <br/>

                            <div className=" col s12">
                                <button className="button "  type="submit" name="action">Log In
                                </button>
                            </div>
                            <br/>
                            <br/>
                            <hr/>
                            <br/>
                            <p><Link to="/">Forgot Password?</Link></p>

                        </div>
                    </form>
                </div>
            </div>
            <div className="mycard">
                <div className="card-panel white auth-card" >
                    <div className="card-body">Don't have an account? <Link to="/signup" className="signup">Sign up</Link></div>
                </div>
            </div>
       </div>
    )
    }
}

export default NewPost;
