const initialState={
    authError:null,
    successMessage:[]
}

const authReducers=(state={initialState},actions)=>{
    switch(actions.type){
        case 'LOGIN_ERROR':
            console.log('login error');
            return{
                ...state,
                authError:'Login Failed'
            }
        case 'LOGIN_SUCCESS':
            console.log('login success');
            return{
                ...state,
                authError:null
            }
        case 'SIGNOUT':
            console.log('signout succesfull');
            return state;
        case 'SIGNUP_SUCCESS':
            console.log('signup successfull');
            console.log(actions.payload);
            return{
                ...state,
                authError:null,
                successMessage:[actions.payload.data.message,'Login to view images posted by your friends!']
            }
        case 'SIGNUP_ERROR':
            console.log(actions.payload);
            return{
                ...state,
                authError:actions.payload.response.data.message,
                successMessage:null
            }
        default:
            return state;
    }
}

export default authReducers;
