import React, { Component } from 'react'
import { Link } from 'react-router-dom';
import axios from 'axios';
import M from 'materialize-css'

class SignUp extends Component {
    state={
        username:'',
        email:'',
        fullName:'',
        password:'',
        uerrors:'',
        eerrors:''
    }

    handleChange=(e)=>{
        this.setState({
            [e.target.id]:e.target.value

        });

        if(e.target.id=='email')
        {
            axios.post('http://192.168.43.13:8080/api/auth/check',{email:e.target.value})
                 .then((res=>{
                     console.log(res);
                 }))
                 .catch(error=>{
                    M.toast({html: error.response.data.message,classes:"red"})
                 })

        }

        if(e.target.id=='username')
        {
            axios.post('http://192.168.43.13:8080/api/auth/check',{username:e.target.value})
                 .then((res=>{
                     console.log(res);
                 }))
                 .catch(error=>{
                    M.toast({html: error.response.data.message,classes:"red"})
                 })

        }
    }


    handleSubmit=(e)=>{
        e.preventDefault();
        axios.post('http://192.168.43.13:8080/api/auth/signup',this.state,{headers:{'Content-Type': 'application/json'}})
            .then(res=>{
                M.toast({html: res.data.message,classes:"green"});
                this.props.history.push('/login');
                M.toast({html: "Login to view images posted by your friends!",classes:"green"});
            })
            .catch(error=>{

                M.toast({html: error.response.data.message,classes:"red"})
            })

    }
    Validation=(e)=>{
        var userRegex=/^[a-zA-Z0-9]+$/;
        var emailRegex=/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/
        if(e.target.id=='username' && e.target.value.length<3 || !userRegex.test(e.target.value))
            this.setState({uerrors:"username must have atleast 3 characters"});
        else    
            this.setState({uerrors:""});

        if(e.target.id=='email' && !emailRegex.test(e.target.value))
            this.setState({eerrors:"username must have atleast 3 characters"});
        else    
            this.setState({eerrors:""}); 
    }


    render() {
        return (
            <div>
            <div className="mycard">
                <div className="card-panel white auth-card" >
                    <div className="card-title">
                        Instagram
                    </div>
                    <div className="card-body">
                    <div>
                    <p>Sign up to see photos and videos <br/>from your friends.</p>
                    </div>
                    <form onSubmit={this.handleSubmit} noValidate>
                        <div className="input-field col s12 border margin" style={{display:'flex'}}>
                                <input  id="username" style={{marginLeft:'10px'}} type="text" className="validate input"  onChange={(e)=>{this.handleChange(e);this.Validation(e)}} />
                                <label htmlFor="username">username</label>
                                {this.state.uerrors!=''?<i style={{marginTop:"8px",color:"red"}} className="material-icons">close</i>:null}
                        </div>
                        <div className="input-field col s12 border margin" style={{display:'flex'}}>
                            <input id="email" type="text" style={{marginLeft:'10px'}} className="validate input" onChange={this.handleChange}/>
                            <label htmlFor="email">Email</label>
                            {this.state.eerrors!=''?<i style={{marginTop:"8px",color:"red"}} className="material-icons">close</i>:null}
                        </div>


                        <div className="input-field col s12 border margin" >
                            <input id="fullName" type="text" style={{marginLeft:'10px'}} className="validate input"  onChange={this.handleChange}/>
                            <label htmlFor="fullName">Full Name</label>
                        </div>

                        <div className="input-field col s12 border margin ">
                            <input id="password" type="password" style={{marginLeft:'10px'}} className="validate input" onChange={this.handleChange}/>
                            <label htmlFor="password">Password</label>
                        </div>
                        <div className=" col s12">
                            <button className="button " type="submit" name="action">Sign Up
                            </button>
                        </div>
                    </form>
                        <br/>
                        <br/>
                        <hr/>
                        <br/>
                        <p>By signing up, you agree to our Terms , Data Policy and Cookies Policy .</p>

                    </div>
                </div>
            </div>
            <div className="mycard">
                <div className="card-panel white auth-card" >
                    <div className="card-body">Have an account? <Link to="/login" className="signup">Log In</Link></div>
                </div>
            </div>
       </div>
        )
    }
}


export default SignUp;
