import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Redirect } from 'react-router-dom';


class Profile extends Component {

    handleClick=(e)=> {
        this.refs.fileUploader.click();
    }
    render() {
     const {auth}=this.props;
     if(!auth.token) return <Redirect to="/login"/>
        return (
            <div style={{maxWidth:"500px",margin:"0px auto"}}>
            <div style={{
               margin:"18px 0px",

            }}>


            <div style={{
                display:"flex",
                justifyContent:"space-around",
                marginBottom:"30px",
               width:"100%"
            }}>
                <div style={{marginTop:"20px",textAlign:"-webkit-center",width:"115px"}}>
                    <div className="row">

                        <img className="image-profile"
                    src={this.props.auth.user.profile} alt="harish"
                    />


                        <div className="row" style={{marginTop:"5px"}}>
                        <button className="profile_button" onClick ={this.handleClick} name="action">change photo</button>
                        <input type="file" id="file" ref="fileUploader" style={{display: "none"}}/>
                        </div>
                    </div>
                </div>
                <div style={{textAlign:"center",margin: "auto"}}>
                <span style={{fontSize:"25px"}}>Harish Jartarghar</span>
                    <div style={{display:"flex",justifyContent:"space-between",width:"100%",marginTop: "15px"}}>
                        <span className="fontsize">40 posts &nbsp;| &nbsp;</span>
                        <span className="fontsize">40 followers&nbsp;| &nbsp;</span>
                        <span className="fontsize">40 following</span>
                    </div>

                </div>
            </div>
             </div>    
             <div className="border-line">
             </div>  

             <div className="row">
                    <div className="column">
                        <img src="https://avatars2.githubusercontent.com/u/51698550?s=460&u=b2a9911e8c6e6dc7f76328e3a3495a60a043b869&v=4" alt="Snow" style={{width:"100%"}}/>
                    </div>
                    <div className="column">
                        <img src="https://avatars2.githubusercontent.com/u/51698550?s=460&u=b2a9911e8c6e6dc7f76328e3a3495a60a043b869&v=4" alt="Forest" style={{width:"100%"}}/>
                    </div>
                    <div className="column">
                        <img src="https://avatars2.githubusercontent.com/u/51698550?s=460&u=b2a9911e8c6e6dc7f76328e3a3495a60a043b869&v=4" alt="Mountains" style={{width:"100%"}}/>
                    </div>
            </div>
            <div className="row">
                    <div className="column">
                        <img src="https://avatars2.githubusercontent.com/u/51698550?s=460&u=b2a9911e8c6e6dc7f76328e3a3495a60a043b869&v=4" alt="Snow" style={{width:"100%"}}/>
                    </div>
                    <div className="column">
                        <img src="https://avatars2.githubusercontent.com/u/51698550?s=460&u=b2a9911e8c6e6dc7f76328e3a3495a60a043b869&v=4" alt="Forest" style={{width:"100%"}}/>
                    </div>
                    <div className="column">
                        <img src="https://avatars2.githubusercontent.com/u/51698550?s=460&u=b2a9911e8c6e6dc7f76328e3a3495a60a043b869&v=4" alt="Mountains" style={{width:"100%"}}/>
                    </div>
            </div>
        </div>
        );
    }
}

const mapStatetoProps=(state)=>{
    return{
      auth:state.auth
    }
}


export default connect(mapStatetoProps)(Profile);
