const mypost=null;
const postReducers=(state={mypost},actions)=>{
    switch(actions.type){  
        case 'POST':
            return{
                ...state,
                post:actions.payload.posts,
                user:actions.payload.user
            }
        default:
            return state;
    }
}

export default postReducers;
