import React, { Component } from 'react'
import { Link } from 'react-router-dom'
import axios from 'axios';
import M from 'materialize-css'

class SignIn extends Component {
    state={
        userfield:'',
        password:'',
        passwordType:'password'
    }

    handleChange=(e)=>{
        this.setState({
            [e.target.id]:e.target.value

        });
    }


    handleSubmit=(e)=>{
        e.preventDefault();
        axios.post('http://192.168.43.13:8080/api/auth/signup',this.state,{headers:{'Content-Type': 'application/json'}})
            .then(res=>{
                M.toast({html: res.data.message,classes:"green"});
                this.props.history.push('/login');
                M.toast({html: "Login to view images posted by your friends!",classes:"green"});
            })
            .catch(error=>{

                M.toast({html: error.response.data.message,classes:"red"})
            })

    }

    render() {
        return (
       <div>
            <div className="mycard">
                <div className="card-panel white auth-card" >
                    <div className="card-title">
                        Instagram
                    </div>
                    <div className="card-body">
                        <div className="input-field col s12 border">
                            <input id="userfield" type="text" className="validate input" />
                            <label htmlFor="userfield">username, or email</label>
                        </div>
                        <div className="input-field col s12 border margin">
                            <input id="password" type="password" className="validate input"/>
                            <label htmlFor="password">Password</label>
                        </div>
                        <div className=" col s12">
                            <button className="button waves-effect waves-light" type="submit" name="action">Log In
                            </button>
                        </div>
                        <br/>
                        <br/>
                        <hr/>
                        <br/>
                        <p><Link to="/">Forgot Password?</Link></p>

                    </div>
                </div>
            </div>
            <div className="mycard">
                <div className="card-panel white auth-card" >
                    <div className="card-body">Don't have an account? <Link to="/signup" className="signup">Sign up</Link></div>
                </div>
            </div>
       </div>
        )
    }
}


export default SignIn;
