import React, { Component } from 'react'
import { Link } from 'react-router-dom';
import axios from 'axios';
import M from 'materialize-css'

class SignUp extends Component {
    state={
        username:'',
        email:'',
        fullName:'',
        password:'',
        message:''
    }

    handleChange=(e)=>{
        this.setState({
            [e.target.id]:e.target.value
        });

        if(e.target.id=='email')
        {
            axios.get('http://192.168.43.13:8080/api/auth/')
        }
    }


    handleSubmit=(e)=>{
        e.preventDefault();
        axios.post('http://192.168.43.13:8080/api/auth/signup',this.state,{headers:{'Content-Type': 'application/json'}})
            .then(res=>{
                M.toast({html: res.data.message,classes:"green"});
                this.props.history.push('/login');
                M.toast({html: "Login to view images posted by your friends!",classes:"green"});
            })
            .catch(error=>{

                M.toast({html: error.response.data.message,classes:"red"})
            })

    }


    render() {
        return (
            <div>
            <div className="mycard">
                <div className="card-panel white auth-card" >
                    <div className="card-title">
                        Instagram
                    </div>
                    <div className="card-body">
                    <div>
                    <p>Sign up to see photos and videos <br/>from your friends.</p>
                    </div>
                    <form onSubmit={this.handleSubmit}>

                        <div className="input-field col s12 border margin">
                            <input id="email" type="text" className="validate input" onChange={this.handleChange}/>
                            <label htmlFor="email">Email</label>
                        </div>
                        <p style={{textAlign:'left'}}>email exist</p>

                        <div className="input-field col s12 border margin">
                            <input id="fullName" type="text" className="validate input"  onChange={this.handleChange}/>
                            <label htmlFor="fullName">Full Name</label>
                        </div>
                        <div className="input-field col s12 border margin">
                            <input id="username" type="text" className="validate input" onChange={this.handleChange} />
                            <label htmlFor="username">username</label>
                        </div>
                        <div className="input-field col s12 border margin ">
                            <input id="password" type="password" className="validate input" onChange={this.handleChange}/>
                            <label htmlFor="password">Password</label>
                        </div>
                        <div className=" col s12">
                            <button className="button " type="submit" name="action">Sign Up
                            </button>
                        </div>
                    </form>
                        <br/>
                        <br/>
                        <hr/>
                        <br/>
                        <p>By signing up, you agree to our Terms , Data Policy and Cookies Policy .</p>

                    </div>
                </div>
            </div>
            <div className="mycard">
                <div className="card-panel white auth-card" >
                    <div className="card-body">Have an account? <Link to="/login" className="signup">Log In</Link></div>
                </div>
            </div>
       </div>
        )
    }
}


export default SignUp;
