const initialState=null;
const authReducers=(state={initialState},actions)=>{
    switch(actions.type){
        case 'LOGIN_SUCCESS':
            console.log('login success');
            return{
                ...state,
                user:actions.payload.user,
                token:actions.payload.token
            }
        case 'LOGGED_IN':
            return{
                ...state,
                user:actions.payload.user,
                token:actions.payload.token
            }

        case 'SIGNOUT':
            console.log('signout succesfull');
            return state;
        case 'LOGGED_OUT':
            return{
                ...state,
                user:null,
                token:null
            }   
        case "PROFILE_UPDATE":
            console.log('reducers',actions.payload.user);
            return {
                ...state,
                user:actions.payload.user
            }
        default:
            return state;
    }
}

export default authReducers;
