import React, { Component } from 'react';
import './App.css';
import NavBar from './components/layouts/NavBar';
import './App.css'
import {Route, Switch, Redirect} from 'react-router-dom';
import Home from './components/screens/Home';
import SignIn from './components/screens/SignIn';
import SignUp from './components/screens/SignUp';
import Footer from './components/layouts/Footer';
import Profile from './components/screens/Profile';
import { connect } from 'react-redux';




class App extends Component {
  render(){





  return (
      <div className="App">
        <NavBar/>
          <RenderRouting LOGGED_IN={this.props.LOGGED_IN}/>
        <Footer/>
      </div>
  );
  }
}

class RenderRouting extends Component{
  render()
  {
    if(localStorage.getItem("jwt"))
    {
        const data={token:localStorage.getItem("jwt"),user:JSON.parse(localStorage.getItem("user"))}
        this.props.LOGGED_IN(data);
        console.log("APP",data);
    }
    return(
        <Switch>
           <Route exact path="/" component={Home}/>
           <Route path="/login" component={SignIn}/>
           <Route path="/signup" component={SignUp}/>
           <Route path="/profile" component={Profile}/>
           <Redirect to="/"/>
        </Switch>
    );
  }

}

const mapDispatchToProps=(dispatch)=>{
  return{
      LOGGED_IN:(data)=>{dispatch({type:'LOGGED_IN',payload:data})},

  }
  }

export default connect(null,mapDispatchToProps)(App);
