const mypost=null;
const postReducers=(state={mypost},actions)=>{
    switch(actions.type){  
        case 'MYPOST':
            return{
                ...state,
                mypost:actions.payload.mypost
            }
        default:
            return state;
    }
}

export default postReducers;
